const math = require("mathjs");

const unitSystems = {
    si: {
        speed: 'm/s',
        distance: 'm',
        time: 's'
    },
    uscs: {
        speed: 'ft/s',
        distance: 'ft',
        time: 's'
    }
};

const generate = (usePredefinedValues = 0) => {
    // 1. Dynamic Parameter Selection
    const selectedSystem = usePredefinedValues ? 'si' : (math.randomInt(0, 2) === 0 ? 'si' : 'uscs');
    const units = unitSystems[selectedSystem];

    // 2. Value Generation
    let v, t;

    try {
        if (usePredefinedValues === 1) {
            // Predefined values for testing
            v = selectedSystem === 'si' ? 25 : 82;  // Speed: 25 m/s for SI, 82 ft/s for USCS
            t = 5;  // Time: 5 seconds
        } else {
            v = math.randomInt(20, 101); // Speed between 20 and 100 units
            t = math.randomInt(1, 11);  // Time between 1 and 10 units
        }

        // 3. Computation Logic
        // Calculate distance using the formula: distance = speed * time
        let distance = v * t; // Distance is in the same unit system as speed and time
        distance = math.round(distance, 3); // Round to 3 decimal places

        // Handle unit conversion if switching between SI and USCS
        if (selectedSystem === 'uscs') {
            distance = math.round(distance * 3.28084, 3); // Convert meters to feet
        }

        // Return the structured data
        return {
            params: {
                v: v,
                t: t,
                unitsSpeed: units.speed,
                unitsDistance: units.distance,
                unitsTime: units.time
            },
            correct_answers: {
                d: distance
            },
            nDigits: 3,
            sigfigs: 3
        };
    } catch (error) {
        console.error('Error during value generation or computation:', error);
        return {
            error: 'An error occurred during computation. Please try again later.'
        };
    }
};

module.exports = { generate };

