import math
import random

def generate(use_predefined_values=0):
    # Define predefined dataset for use_predefined_values = 1
    predefined_data = [
        {"v": 50, "unitsSpeed": "km/h", "t": 2, "unitsTime": "h"},
        {"v": 60, "unitsSpeed": "km/h", "t": 1.5, "unitsTime": "h"},
        {"v": 80, "unitsSpeed": "km/h", "t": 1, "unitsTime": "h"},
        {"v": 100, "unitsSpeed": "km/h", "t": 0.5, "unitsTime": "h"},
        {"v": 70, "unitsSpeed": "km/h", "t": 2.5, "unitsTime": "h"}
    ]

    # Select random values or predefined values
    if use_predefined_values == 0:
        try:
            v = round(random.uniform(20, 120), 3)  # Speed in km/h
            t = round(random.uniform(0.5, 5), 3)    # Time in hours
            unitsSpeed = 'km/h'
            unitsTime = 'h'
        except Exception as e:
            raise RuntimeError(f"Error generating random values: {e}")
    else:
        try:
            selected_values = predefined_data[random.randint(0, len(predefined_data) - 1)]
            v = selected_values["v"]
            t = selected_values["t"]
            unitsSpeed = selected_values["unitsSpeed"]
            unitsTime = selected_values["unitsTime"]
        except IndexError:
            raise RuntimeError("Error accessing predefined values: Index out of bounds")

    # Calculate distance using the formula: Distance = Speed * Time
    # $d = v \cdot t$
    distance = v * t

    # Create parameters and answers dictionary
    params = {
        "v": v,
        "unitsSpeed": unitsSpeed,
        "t": t,
        "unitsTime": unitsTime,
        "distance": round(distance, 3),
        "unitsDist": "km"
    }

    correct_answers = {
        "d": round(distance, 3)
    }

    return {
        "params": params,
        "correct_answers": correct_answers,
        "nDigits": 3,
        "sigfigs": 3,
    }
