const math = require("mathjs");

const generate = (usePredefinedValues = 0) => {
    const unitSystems = {
        si: {
            unitsMass: 'kg',
            unitsAcceleration: 'm/s²'
        },
        uscs: {
            unitsMass: 'lb',
            unitsAcceleration: 'ft/s²'
        }
    };

    // Select unit system
    const unitSystemKeys = Object.keys(unitSystems);
    const selectedUnitSystem = unitSystems[unitSystemKeys[math.randomInt(0, unitSystemKeys.length)]];

    let m1, theta, mus, muk, m2;

    // Generate random parameters if usePredefinedValues is not set
    if (usePredefinedValues === 0) {
        m1 = math.randomInt(5, 101); // Mass m1 between 5 and 100 kg or lb
        theta = math.randomInt(10, 31); // Angle between 10 and 30 degrees
        mus = math.round(math.random() * (1.0 - 0.1) + 0.1, 2); // Static friction coefficient
        muk = math.round(math.random() * (1.0 - 0.1) + 0.1, 2); // Kinetic friction coefficient
        m2 = math.randomInt(5, 101); // Mass m2 between 5 and 100 kg or lb
    } else {
        // Use predefined test values
        m1 = 50; // kg
        theta = 20; // degrees
        mus = 0.5; // static friction coefficient
        muk = 0.3; // kinetic friction coefficient
        m2 = 70; // kg
    }

    // Error handling: Check if parameters are valid
    if (m1 <= 0 || m2 <= 0 || mus < 0 || muk < 0 || mus >= 1 || muk >= 1) {
        throw new Error("Invalid input parameters generated.");
    }

    // Performing calculations based on physics
    const g = selectedUnitSystem.unitsMass === 'kg' ? 9.81 : 32.2; // Gravity
    const weight1 = m1 * g; // Weight of block m1
    const weight2 = m2 * g; // Weight of block m2
    const friction1 = mus * weight1; // Max static friction

    // Calculate net force acting on m2
    const netForce = weight2 - weight1 * math.sin(math.unit(theta, 'deg').toNumber('rad')) - friction1;

    // Check if netForce is less than 0 to avoid negative acceleration
    if (netForce < 0) {
        throw new Error("Net force calculated as negative, indicating a physical impossibility.");
    }

    // Acceleration of m2 (F = ma => a = F/m)
    const a = netForce / m2;

    // Round the acceleration to the desired number of significant figures
    const roundedAcceleration = math.round(a, 3);

    return {
        params: {
            m1: m1,
            theta: theta,
            mus: mus,
            muk: muk,
            m2: m2,
            unitsMass: selectedUnitSystem.unitsMass,
            unitsAcceleration: selectedUnitSystem.unitsAcceleration
        },
        correct_answers: {
            a: roundedAcceleration
        },
        nDigits: 3,
        sigfigs: 3
    };
};

module.exports = { generate };
