const math = require("mathjs");

const generate = (usePredefinedValues = 0) => {
    // 1. Dynamic Parameter Selection
    const unitSystems = {
        si: {
            speed: 'km/h',
            distance: 'm',
            time: 'h'
        },
        uscs: {
            speed: 'mph',
            distance: 'ft',
            time: 'h'
        }
    };
    const selectedSystem = math.randomInt(0, 2) === 0 ? 'si' : 'uscs';
    const units = unitSystems[selectedSystem];

    // 2. Value Generation
    const v = (usePredefinedValues === 0) ? 100 : 100; // constant speed 100 mph or equivalent
    const t = (usePredefinedValues === 0) ? 5 : 5; // constant time 5 hours

    // 3. Computation Logic
    let distance;
    if (units.speed === 'mph') {
        distance = v * t; // Distance = Speed * Time
    } else {
        // convert mph to km/h
        distance = v * t * 1.60934; // 1 mph = 1.60934 km/h
    }
    distance = math.round(distance, 3); // Round to 3 decimal places

    // Return the structured data
    return {
        params: {
            v: v,
            t: t,
            unitsSpeed: units.speed,
            unitsTime: units.time,
            unitsDistance: units.distance
        },
        correct_answers: {
            d: distance
        },
        nDigits: 3,
        sigfigs: 3
    };
};

module.exports = { generate };

